package java_Package;

public class Data_types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b=20;
int sum = a+b;

int mult = a*b;

int division = b / a;

int substract = b - a;

int mod = b%a;

System.out.println("The sum of two numbers: " +sum);

System.out.println("The multiplication of two number: " +mult);

System.out.println("The division of two numbers: " +division);

System.out.println("The substraction of two numbers: " +substract);

System.out.println("The modulus value "+mod);

	}

}
