package java_Package;

public class If_Else_example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a = 13;

int mod = a%2;

if (mod == 0)
{
System.out.println("This number is even: " +a);
	
}
else
{
System.out.println("This number is odd: " +a);	
}

	}

}
